import { post } from '@itellyou/itellyou-request';
import { dataURLToImage } from './utils';

/**
 * @fileOverview 编辑态
 */
export default {
  // 渲染编辑视图
  _renderEditView() {
    if (!this.page) {
      this.renderPage();
    }
    this._normalizeValue();
    this._renderResizeController();
    this._renderTool();
    this._bindEditEvent();
    this._readValue();
    this.page.changeMode('none');
    this._renderImage();
    this.pageContainer[0].style.visibility = 'hidden';
    this.imageContainer.show();
  },
  _normalizeValue() {
    const { graphData, src } = this.value;
    if (!graphData && !src) {
      this.setValue(
        {
          src:
            'data:image/png;base64,',
        },
        false,
      );
    }
  },
  // 绑定编辑态事件
  _bindEditEvent() {
    const { page, engine } = this;
    this.graphEditor.on('aftercommandexecute', ({ command }) => {
      if (this.getChangeDataCommandNames().indexOf(command.name) !== -1) {
        this.isChanged = true;
        this.setValue(
          {
            graphData: page.save(),
          },
          true,
        );
      }
    });
    // 拖动画布结束后记录视口
    page.on('afterpancanvas', () => {
      if (!this.state.maximize) {
        this.setValue({
          matrix: page.getMatrix(),
        });
      }
    });
    page.on('statuschange', () => {
      engine.toolbar.updateState();
    });
    // 在 value 里存储截图链接
    this.bindEvent(this.engine.asyncEvent, 'save:before', () => {
      if (this.isChanged) {
        // 存图片
        const saveImage = page.saveImage();
        const base64 = saveImage.toDataURL('image/png', 2.0);
        const formData = new FormData();
        formData.append(
          'file',
          dataURLToImage(window.atob(base64.replace(/^data:image\/png;base64,/, ''))),
          'mindmap.png',
        );
        this.isChanged = false;
        return post(this.engine.options.image.action, undefined, {
          headers: {
            // content-type 由 FormData 自行设置，此处置为 undefined
            'Content-Type': undefined,
          },
          body: formData,
        }).then(({ result, data, message }) => {
          if (result) {
            this.setValue(
              {
                src: data.url,
                search: this.searchContent,
              },
              false,
            );
          } else {
            this.engine.messageError(message);
          }
        });
      }
    });
  },
};
